mapper.select:
SELECT *, (SELECT COUNT(*) FROM boardview WHERE bcate=#{board})AS COUNT
FROM (SELECT CAST(@ROWNUM:=@ROWNUM+1 AS UNSIGNED)AS rowNum, b.*
	FROM boardview b, (SELECT @ROWNUM:=0) AS R
	WHERE b.bcate = #{board}
	ORDER BY b.bno ASC
	) mm
ORDER BY mm.bno DESC
LIMIT #{pageNo}, #{contentNo}

mapper.update:
UPDATE board 
SET bdel=0 
WHERE bno=#{bno}

UPDATE board 
SET btitle=#{title},bcontent=#{content}
WHERE bno=#{bno}

UPDATE board
SET bread=bread+1
WHERE bno=#{bno}

UPDATE comments 
SET cdel=0
WHERE bno=#{bno} AND cno=#{cno}

UPDATE comments 
SET comment=#{recomment}
WHERE bno=#{bno} AND cno=#{cno}

mapper.select:
SELECT stitle
FROM setupboard
WHERE bcate=#{board}

SELECT COUNT(*)
FROM boardview
WHERE bcate=#{board}

SELECT mid
FROM comments
WHERE cno=#{cno}

SELECT COUNT(*)
FROM noticeview
WHERE ncate=#{board}

SELECT *, (SELECT COUNT(*) FROM noticeview WHERE ncate=#{board})AS COUNT
FROM (SELECT CAST(@ROWNUM:=@ROWNUM+1 AS UNSIGNED)AS rowNum, b.*
	FROM noticeview b, (SELECT @ROWNUM:=0) AS R
	WHERE b.ncate = #{board}
	ORDER BY b.nno ASC
	) mm

ORDER BY mm.nno DESC
LIMIT #{pageNo}, #{contentNo}

mapper.update:
UPDATE notice
SET nread=nread+1
WHERE nno=#{nno}

UPDATE notice
SET ntitle=#{title},ncontent=#{content}
WHERE nno=#{nno}

UPDATE notice
SET ndel=0 
WHERE nno=#{nno}
