mapper.select:
select b.bno, b.btitle, b.bcontent, DATE_FORMAT(b.bdate, '%Y-%m-%d') AS bdate, b.bcate, (select count(*) from board b JOIN members m on b.mno = m.mno
m.mid = #{mid} and b.bdel = 1 and b.bcate = #{cate}
and b.btitle like concat('%', #{searchV},'%') and b.bcontent like concat('%', #{searchV},'%')
) as count from board b join members m on b.mno = m.mno
m.mid = #{mid} and b.bdel = 1 and b.bcate = #{cate}
and b.btitle like concat('%', #{searchV},'%') and b.bcontent like concat('%', #{searchV},'%')
order by b.bno desc LIMIT #{cri.pageStart}, #{cri.perPageNum}

SELECT count(*) FROM board b join members m on b.mno = m.mno
m.mid = #{mid} and b.bdel = 1 and b.bcate = #{cate}
and b.btitle like concat('%', #{searchV},'%') and b.bcontent like concat('%', #{searchV},'%')

select btitle, bcontent, bdate, bread, bno
from board
where bno = #{bno}

select c.cno, c.comment, DATE_FORMAT(c.cdate, '%Y-%m-%d') AS cdate, DATE_FORMAT(b.bdate, '%y-%m-%d %h:%m') AS bdate, b.bcate, b.btitle, (select count(*) from comments c join members m on c.mno = m.mno
m.mid = #{mid} and c.cdel = 1 and b.bcate = #{cate}
and b.btitle like concat('%', #{searchV},'%') and c.comment like concat('%', #{searchV},'%')
) as count from comments c join members m on c.mno = m.mno join board b on c.bno = b.bno
m.mid = #{mid} and c.cdel = 1 and b.bcate = #{cate}
and b.btitle like concat('%', #{searchV},'%') and c.comment like concat('%', #{searchV},'%')
order by c.cno desc LIMIT #{cri.pageStart}, #{cri.perPageNum}

SELECT count(*) FROM comments c join members m on c.mno = m.mno join board b on c.bno = b.bno
m.mid = #{mid} and c.cdel = 1 and b.bcate = #{cate}
and b.btitle like concat('%', #{searchV},'%') and b.bcontent like concat('%', #{searchV},'%')

mapper.update:
update comments set cdel = 0 
where cno=#{cno}
and mno=(select mno from members where mid=#{mid})

mapper.select:
select mid, mname, maddr, mbirth, mphone, memail
from members
where mid = #{mid}

mapper.update:
update members set

		mid = #{id}
		mpw = #{pw}
		mbirth = #{birth}
		maddr = #{addr}
		memail = #{email}
		mphone = #{phone}


	mno = #{mno}

mapper.select:
select count(*) as count
from members
where mid = #{id}

select count(*) as count
from members
where memail = #{email}
